package day10_forLoop;

public class Task2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int sum = 0;

	       for (int i = 1; i <= 100; i++) {
	         sum += i;
	       }
	       System.out.println("The sum of all numbers between 1 and 100 is: " + sum);

	}

}
